select bp.run_id, res.id_ as task_id, res.execution_id_ as execution_id, res.proc_inst_id_ as instance_id, res.proc_def_id_ as definition_id, res.name_ as node_name, res.task_def_key_ as node_id, bp.business_id, bp.business_title, bp.start_user_id, bp.create_time, bp.process_key, bp.process_name, bp.category_id, bp.category_name, bp.business_status from act_ru_task res left join act_ru_identitylink i on i.task_id_ = res.id_ left join ( select run.id as run_id, run.instance_id, run.business_id, run.business_title, run.start_user_id, run.business_status, run.create_time, def.process_key, def.process_name, def.category_id, cat.`name` as category_name from bpm_process_run run left join bpm_process_definition def on run.definition_id = def.definition_id left join bpm_category cat on def.category_id = cat.id ) bp on bp.instance_id = res.proc_inst_id_
( res.assignee_ = #{request.assigneeId} or ( res.assignee_ is null and i.type_ = 'candidate' and i.user_id_ = #{request.assigneeId} )
or ( res.assignee_ is null and i.type_ = 'candidate' and i.group_id_ in
#{item}
)
)
AND bp.process_name LIKE concat('%', #{request.processName}, '%')
AND bp.start_user_id = #{request.startUserId}
AND bp.category_id = #{request.categoryId}
AND bp.business_status = #{request.businessStatus}
ORDER BY res.create_time_ DESC

SELECT pr.id AS run_id, pr.definition_id, pr.instance_id, pr.business_id, pr.business_title, pr.start_user_id, pr.business_status, pr.tenant_id, pr.create_time, pr.end_time, pr.duration, pd.process_name, pd.category_id, bc.`name` AS category_name FROM bpm_process_run pr LEFT JOIN bpm_process_definition pd ON pr.definition_id = pd.definition_id LEFT JOIN bpm_category bc ON bc.id = pd.category_id WHERE pr.start_user_id = #{request.startUserId}
AND bp.process_name LIKE concat('%', #{request.processName}, '%')
AND bp.category_id = #{request.categoryId}
AND bp.business_status = #{request.businessStatus}
ORDER BY pr.create_time DESC
